UPDATE cafe
SET cafeTel = #{cafeTel}
WHERE cafeId = #{cafeId}

UPDATE cafe
SET address = #{address},longtitude = #{longtitude} ,latitude = #{latitude}
WHERE cafeId = #{cafeId}

UPDATE cafe
SET startTime = #{startTime},endTime = #{endTime}
WHERE cafeId = #{cafeId}

UPDATE cafe
SET study = #{study}
WHERE cafeId = #{cafeId}
