mapper.update:
UPDATE reservation
SET status = 'P', modifyDate = sysdate
WHERE reservationId = #{reservationId}

UPDATE reservation
SET status = 'N', cancleReasonId = #{cancleReasonId}, modifyDate = sysdate
WHERE reservationId = #{reservationId}

UPDATE reservation
SET status = 'F', modifyDate = sysdate
WHERE reservationId = #{reservationId}

mapper.select:
select *
from (
         select *
         from reservation
         where userid = #{userId}
         order by createDate DESC, TO_DATE(reserveStart, 'HH24:MI') ASC
     )
where ROWNUM = 1

select reservationId
from reservation
where userId = #{userId}
and reserveDate = #{reserveDate}
and reserveStart = #{reserveStart}
