mapper.select:
SELECT distinct c.cafeId, c.cafeName, c.cafeType, c.startTime, c.endTime, c.address, c.longtitude, c.latitude, c.cafeTel, c.study FROM cafe c LEFT JOIN ( SELECT CAFEID, FEATUREID from CAFEFEATURE group by CAFEID, FEATUREID ) cf ON c.cafeId = cf.cafeId LEFT JOIN ( SELECT FEATUREID, FEATURENAME from FEATURE group by featureName,featureId ) f ON cf.FEATUREID = f.FEATUREID WHERE c.study = #{studyEnable}
AND c.cafeType = #{cafeType}
AND c.cafeType IN ('G','P')
AND EXISTS ( SELECT 1 FROM cafeTable ct WHERE ct.cafeId = c.cafeId AND ct.tableType = #{preferSeat} )
AND TO_DATE(c.startTime, 'HH24:MI') <= TO_DATE(#{startTime}, 'HH24:MI') AND TO_DATE(c.endTime, 'HH24:MI') >= TO_DATE(#{endTime}, 'HH24:MI')
AND SYSDATE >= TO_DATE(c.startTime, 'HH24:MI') AND SYSDATE <= TO_DATE(c.endTime, 'HH24:MI')
AND f.featureName IN
#{feature}
AND c.CAFENAME LIKE '%'||#{word}||'%'
